function printDungeon() {
    let creator = "";
    let purpose = searchArray(Object.keys(dungeon.Purpose));
    if (rollDice(100) < 50) {
        creator = searchArray(dungeon.Creator.Basic);
    } else {
        let creatorAlig = searchArray(dungeon.Creator.Human.Alignment);
        let creatorClass = searchArray(dungeon.Creator.Human.Class);
        creator = `a humaniod, specifically a ${creatorAlig} aligned ${creatorClass},`;
    }


    function dungeonBuilder(purpose) {
        let roomCount = 0;
        let limiter = 50
        function start() {
            let inde = Math.floor(Math.random(1) * dungeon.Layout["Starting area"][0].length);
            let start = dungeon.Layout["Starting area"][0][inde];
            roomCount += dungeon.Layout["Starting area"][1][inde];
            return `The entrance of this dungeon lets into a ${start}, likely this area was a(n) ${searchArray(dungeon.Purpose[purpose])}.`
        };
        let output = `There is an area of interest ${searchArray(dungeon.Location)}, it was created by ${creator} and historically it has been used as a ${purpose}. Also, ${searchArray(dungeon.History)}.`
        console.log(output + "\n\n" + start())
        
        let newRooms = [roomCount]
        let outputArray = []
        let counter = 1
        for (t = 1; roomCount < limiter; t++) {

            function determineType(num, x) {
                let Arrg = []
                for (i = x; i < num; i++) {

                    let chance = rollDice(100)
                    if (chance < 25) {
                        Arrg.push(`Path ${toWords(counter)}is ${searchArray(["sealed", "blocked by rubble", "caved in", "too small to enter"])}`)
                        counter++
                    } else if (chance < 75) {
                        if (roomCount > limiter) {
                            let size = rollDice(1)
                            let chamber = dungeon.Layout.Chambers.Shape[size][Math.floor(Math.random(1 * dungeon.Layout.Chambers.Shape[size].length))]
                            let exits = 0
                            roomCount += exits
                            Arrg.push(`Path ${toWords(counter)}leads to a chamber that is ${chamber}, seeming to be used as a ${searchArray(dungeon.Purpose[purpose])} with ${exits} exits.`)
                            counter++
                        } else {
                            let size = rollDice(1)
                            let chamber = dungeon.Layout.Chambers.Shape[size][Math.floor(Math.random(1 * dungeon.Layout.Chambers.Shape[size].length))]
                            let exits = searchArray(dungeon.Layout.Chambers["Chamber Exits"][size])
                            roomCount += exits
                            Arrg.push(`Path ${toWords(counter)}leads to a chamber that is ${chamber}, seeming to be used as a ${searchArray(dungeon.Purpose[purpose])} with ${exits} exits.`)
                            counter++
                        }
                    } else {
                        let inde = Math.floor(Math.random(1 * dungeon.Layout.Passage.length));
                        let passage = "";
                        let exits = "";
                        let size = searchArray(dungeon.Layout["Passage width"]);

                        function passType() {
                            let chance = rollDice(100)
                            if (chance < 75) {
                                passage = 'passage that is ' + dungeon.Layout.Passage[inde];
                                exits = dungeon.Layout.PassageCount[inde]
                            } else {
                                passage = 'staircase that is ' + searchArray(dungeon.Layout.Stairs)
                                exits = 1
                            }
                        }
                        passType()
                        roomCount += exits
                        Arrg.push(`Path ${toWords(counter)}leads to a ${size +" "+ passage} with ${exits} connection(s)`)
                        counter++
                    }
                }
                return Arrg
            }
            let build = determineType(roomCount, counter);
            let bog = [].concat(outputArray, build)
            outputArray = bog

            newRooms.push(roomCount)
        }
        printArray(outputArray)
    };
    dungeonBuilder(purpose)

};

function tier(){
    let tier = ["Tier 1 (lvl 1-5)", "Tier 2 (lvl 6-10)", "Tier 3 (lvl 11-16)", " Tier 4 (17-20)", "Marathon (Tier 1-4: level 1-20)"]
    document.getElementById("Tier").innerHTML = searchArray(tier);
};

function flavor(){
    let flavor = ["Heroic", "Sword and Sorcery", "Epic", "Mythic", "Dark", "Intrigue", "Mystery", "Swashbuckling", "War", "Wuxia (orient)",'Horror','Discovery','Historic','Time Travel','Plane Travel','Steam Punk','Calamity']
    document.getElementById("Flavor").innerHTML = searchArray(flavor);
};

function start() {
    let startOfConflict = [
        `While travelling in the wilderness, the characters fall into a sinkhole that opens up beneath their feet, dropping thme into the adventure location.`,'While travelling in the wilderness, the characters notice the entrance to the adventure location',`While travelling on a road, the characters are attacked by monsters that flee into the nearby adventure location.`,`The avdneturers find a map on a dead body. In addition to the map setting up the adventure,  the adventure's villain wants the map`,`AQ mysterious magic item or a cruel villain teleports the characters to the adventure lcoation`,`A stranger approaches the party in the tavern and urges them to the adventure location`,'A town or village needs volunteers to go to the adventure location','An NPC the characters care about needs them to go to the adventure location','An NPC the players must obey orders them to the adventure location','An NPC the party respects asks them to go to the adventure location','One night, the characters all dream about the adventure location','A ghost appears and terrorizes a village. Research reveals it can only be put to rest by going to the adventure location.',
        "A Child is born", "It's an Ambush", "It's someone's Birthday", "A Festival is in full swing", "A Monarch is being Coronated", "A Tournament is about to begin", "There is a Migration of Monsters", "It's the Day of an Important Trial", "Someone is undergoing investigation", "The PCs have been invited to a Ball", "An important Treaty is being signed", "A Mysterious Murder has taken place", "Two Planes are temporarily Converging", "It's the Anniversary of an Important Event", "The PCs have been unexpectedly drugged", "A Funeral Procession passes through town", "A Mysterious Stranger sits alone in the tavern", "Someone important is being publicly executed", "An NPC the PCs care about has been kidnapped", "The PCs have been wrongfully accused of a crime", "Ask everyone to roll a Dexterity save immediatly, without any context. After they've all rolled, describe the scene. They're running away from a hill giants' settlement after stealing something valuable. The plan was to sneak in, take the object, and leave silently, but a giant caught them red-handed and had to start running for their lives. Now the giants are throwing huge rocks at them from the distance while they run down the mountain. Make them roll once or twice more before succesfully escape and nerf the rock damage a bit if it hits. NPC, yelling at a PC while running: FOR THE GODS SAKE TELL ME YOU STILL HAVE IT WITH YOU!", "There you are, studying the situation. Tension can be cut with a knife, silence, no other sound but your steps on the grass and the growls of that damn thing. You walk in circles, keeping an eye and distance between you, and them, and thinking how on earth could you have ended up in that situation. You were facing a wild manticore 30 seconds ago, but suddenly a group of bandits appeared from nowhere, interrupting the scene. The situation just turned into a three-way combat, and everyone is just waiting for the others to make a move. Roll initiative, my dears.", 
        "The blizzard is hitting hard on them. They should've stayed in the village until tomorrow, but now it is too late to regret. As they walk through this narrow mountain pass, exhausted, deafened by the wind and almost blinded by the snow, one of the PCs slips. He/she manages to clutch to the edge of the cliff, but struggles to avoid falling.", "The party is being chased by a huge zombie horde in the middle of a foggy forest, when suddenly one of the PCs spots a small abandoned cabin. Getting inside seems like the best option in that dire situation. Anyways, the cabin is only a temporary solution because the zombies will end up entering one way or another; luckily, it has a small trapdoor leading to a basement...", "The PCs start tied in a ship's deck and one of them is about to walk the plank. Luckily, another PC just managed to get free while the crew is distracted with the show and mocking.", "The party are passengers on a ship that has come under attack by a sea monster, roll various checks in an attempt to survive. Eventually the ship is destroyed and the party will wash up on shore (whether they made it aboard a life raft or not).", "The PCs stare dumbly at each other in a quiet corner of a chaotic battlefield. They have no idea whose on what side, but they have a bigger problem: one of the players receives a reply from a sending scroll that goes: 'Roger that, Charlie Unit. Meteor Strike Inbound, eta 5 minutes'.", "An alarm is blaring. In the dimly-lit barracks, a flashing light covers the entire room in blinding crimson every two seconds. Before you’ve even fully woken up, you find yourself and your companions being aggressively shouted-at in a foreign tongue.", 
        "You feel like you've been running through the woods for hours, and your pursuers never seem to drop back very far. You come across a stream that seems to flow in about the direction you're trying to go, so you jump into it to try to hide your scent. Unfortunately, that narrow stream turns out to be a narrow but deep, fast-flowing river, and you're swept away on the current. Roll a Dexterity save for half damage as you bang against the walls of the narrow torrent/get projected over the edge of a cliff in a waterfall.", "The lunar festival is going swimmingly. Everyone is drinking and dancing and having a merry time. The PC's are enjoying the festivities in their own way. You notice that a fight has started near the band, and another one near the tables, wait another fight has broken out. As you look closer, the festival goers seem to be transforming into flesh craving demons. Chaos has erupted at this peaceful festival. Roll initiative!", 
        "In the dazzling aftermath of the massive flash of the spell going off, you shake your heads and rub your eyes. Your memory of the previous couple of days is missing and some other ship is attacking yours....! Roll initiative!", "The party is riding along, singing, talking and enjoying a fine day until. Twang! An arrow comes shooting out of the woods and burying itself at the ground at their feet. Figures move in the shadows and steel glimmers. A lone figure steps out with bow and cowl draw.", "As you round the familiar hill, the woods surrounding your hometown become visible. Your mouth waters with the instinctive anticipation of the first pint. Before your smile can reach the full corners of your mouth, your heart sinks as a billowing cloud of smoke begins to rise above the top of the trees followed by the sounds of terrified screams. Unconscious of when you began, you realize your companions are also running toward home at a dead sprint while similarly drawing their own weapons.", "The party is walking through the capital city. As they pass a shadowed alley, a young woman in servant’s clothing stumbles out of the darkness and into them, crumpling to the ground. A nasty wound stains her side and she clutches a cloth bundle to her chest. In distance you hear the bells of the city start to ring and hounds baying. She whispers 'The ruler] is dead! Please get it/her/him] to safety. Cryptic hint]. Go, I’ll lead them away!!' She climbs back to her feet and runs away. The sound of the dogs grows louder.", "It’s a sunny day and the weather is perfect for flying. The airship the party has booked passage on catches the perfect wind and the sails snap and bell in the gust, carving through the sky. Suddenly a boulder smashes into the side of the ship - you’ve wandered into the territory of a stone giant clan and they’re looking to bring you down.", "The air fills with an electric buzz and your teeth start to hurt. A portal explodes open next to the party, knocking them all to the ground. A glowing form tumbles out and the portal slams shut with a thunderclap. As the party regains their feet, they see the mortally wounded solar gasp its last breath then dissolve into glowing mist which swiftly dissipates. On the ground, covered in glowing ichor and formed from some unknown crystal, lies a larger version of the cleric/paladin/monk]’s deity.", "PCs are patroling snowy mountains when they encounter a stranded galley.", 
        "PCs are at the funeral of one of the PCs' brother. Soon they realize it was a murder.", "PCs live in small village when everyone is forced to evacuate due to approaching invading army.", "The party starts as slaves to a massive organization of Ogres who are looking to put the characters into a salt mine for the rest of their lives. As the guard has his back turned for a moment, the party catches a glimpse of a key ring around his belt. Could that unlock their shackles?", "A merchant sold each party member a lemon item- a cracked hammer, tin-plated armor, etc. The party meets outside his shop, which is ‘coincedentally’ closed.", "Someone dug a deep pit trap in the road, and a character (or someone else) has fallen in. The party gets together to help them out.", "The party starts as gladiators and must fight each other. Either money or slavery led them to the colosseum.", "The group are all on a caravan together travelling from one town to a nearby city. During the day, bandits/orcs raid the caravan, killing and burning some of the wagons before they take off into the wilderness. The group emerges from the ruins of their caravans and are forced into a makeshift group… do you search after the raiders for revenge? Get back items stolen? Or do you continue on your way because you have more important business/mission to attend to?", "They are all framed for the same murder/robbery, and meet in jail.", "The party all have ties to a specific Church. They may be members of the Clergy, Enforcers, or someone who has a negative tie to said Church.", "The party is told about a new tavern that is offering free drinks for it’s grand opening. They all attend and get Shanghaied. They wake up on a slave ship with no equipment and have to escape or take control of the ship.", "A party member (or several) are all aboard a trade ship headed across the sea to deliver rare goods, when their ship is swallowed by a giant whale/sea monster! Inside it’s gullet are the rest of the party and several other rough-looking sea folk in what looks to be a small town hobbled together out of several shipwrecks.", "Each character steps into a magical portal leading (players can choose intended destination). A random teleportation mishap puts them together in the bottom of a cave, holding each other’s gear.", "A strange plague has run rampant through the town, turning nearly everyone to stone. Your party were unaffected and meet in the silent inn, filled with statues.", 
        "Each party member is being blackmailed (using their respective bonds). The blackmailer has gathered them together with the offer of a score big enough to wipe away their debt.", "The inn the characters are staying at is shockingly packed. They are forced to share a table. A local tough claims it is her reserved table and demands the party leave, or face her gang…", "The party each owe an important debt to another party member (have them work out whom owes whom).", "The party are stuck in the cellar of an evil wizard. Each of them (for their own purposes) attempted to break into the tower and were caught by security.", "Each party member has been having strange, recurring dreams involving the party as great heroes. They have never met each other before- until this chance meeting on the road…", "The party wakes up chained to each other in a sandstone room with a portcullis to one side letting in a very bright light, the sound of thousands of people cheering right above them. It takes them a second to process that they’re about to fight another party/a giant creature in gladiatatorial combat.",
        "You’re all at a funeral when the deceased comes back to life and lunges at the grieving family.", "You come to consciousness. Your eyes open, and there’s an immediate numb feeling in your arms. You glance at them, but you’re not laying on your back. You’re chained up. Your body slumps forward with a fierce pain in your shoulders. Chains clamped around your wrists, you look down. Your legs are also chained, not together, but to a wall. Panic sets in as you glance up you see other figures, your friends, all in the same predicament, except one man who seems to be in an iron cage. They are all unconscious to, slowly coming round. You have no gear, you’re stripped back to just your clothes.", "The party all arrive at a dungeon at the same time, to answer the same advertisement asking for help retrieving an artifact. When they start to argue about whether or not they should work together and split the reward, they get ambushed by a small group of goblins.", "The party were all passengers on the same ship, when it was caught in a storm. Washing up on shore amping the wreckage, they seem to be the only survivors.", "Party is chained together, walking behind a wagon. One of the guards mentions that you are all going to be sacrificed.", "As children, you were each enrolled in a cross-cultural pen pal association. You are meeting the other writers for the first time as adults.", 
        "You are all victims of a doppelganger who stole each of your identities.", "The players are invited to the reading of the will of [Random Player’s] Great-Uncle Thrice removed. When they and the rest of the party show up, they discover that Great Uncle Thrice had been something of an adventurer themselves – why, they once acquired the Mysterious MacGuffin from the Temple of the Red Herring! They also discover that they weren’t the only heavily armed group to show up….", "The party has been asked to help protect a traveling carnival going to the next city over. All is well until one day, the strongman is found wounded in his tent, claiming a member of the carnival is was taken into the woods by whatever attacked him.", "The party was previously tasked to steal something from a primitive tribe. They managed to take it, but were caught, and now are running away.", "The party is in an inn, and suddenly people outside start to scream, and a person kicks the door, screaming “Dragon!”.", "The party is attending to a mutual friend’s wedding. Something bad happens during the event, and drives the party into adventure.", "The party is in a big castle, attending some sort of event. A random fire begins, and threatens to quickly destroy the whole building. The party must escape, as they decide who, among all the people in danger, they are gonna save.", 
        "The city is burning. Enemies run through the streets, slaughtering all the citizens they see, with little to no resistance. Luckily, the party members weren’t attacked yet, but they must find a way to leave. All of the city gates are guarded by enemy soldiers.", "The party is approached by a man who claims to be stuck in a time loop [think Groundhog Day]. He says they already helped him before, and is very close to figuring out how to break free from the loop. He is able to provide the party with any evidence they ask for.", "The party hears word of a man who claims to be a prophet of the apocalypse. The man was arrested for disturbing the peace, but the party begins to find evidence for his predictions coming true… [that doesn’t mean there will be an apocalypse, just that the man predicted many elements of the plot’s main conflict.]", "A serious infection broke out in a big city. Authorities set a quarantine, but the party sees a group of people escaping.", "There are shooting stars in the night sky. They make an incredible light show, that makes most of the citizens stop what they are doing to watch. When it all ends though, people come to their homes to find that their valuable possessions disappeared.", "The party is next to a city square, where an opposition leader is making a speech to a big crowd. Suddenly, soldiers [who may or may not be aligned with the government] attack the crowd.", "The group has been pressed into naval service by a corrupt baron and needs to find a way to jump ship.", "The group (who haven’t met each other yet) are all enjoying their drinks at a tavern when a gang of ruffians breaks down the door demanding protection money. After the situation is resolved, the group finds themselves in trouble with the law.", "A mysterious figure recruits each of the group members for a heist, only to betray them once the job is done.", 
        "While sitting in a tavern a man approaches a member of the party and kneels before them. Crying the man tells him he is thankful to see the rightful Lord is alive.", "The party finds, to their surprise, that they are the only non-cultists staying at the inn.", "The party are all young citizens of a small town and as their “coming of age” rite, they must explore the tomb of a local hero and bring back an etching from his casket. What they don’t know, is that every rite like this that takes place in the town is more of a game that is set up by the mayor and other officials and townsfolk who have set clever tasks in order to challenge the would be adventurers.", "The party start as guards to a noble’s gathering on the eve of a revolutionary uprising.", "The lord is holding a grand tournament. From near and far, people come to participate in the melee and/or archery contests, while the glorious knights come to joust. Along the main road to town stands a huge popular tavern/inn, about a day’s ride from the tournament. It is bursting with travelers on their way to the tournament, among the visitors of course the characters.", "The characters wake up aboard a plague cart. Their bodies are about to be dumped in a mass grave.", "The characters are all at the town square to witness the execution of a notorious bandit leader. The bandit’s companions launch a rescue operation without regard to the safety of the audience. (Option: When the hood is drawn off the bandit – or the executioner – one character is shocked to see it’s a relative.)", "The characters have all grown up together in a small farming community and are expected to take over their fathers’ work. However, one rainy spring evening, a knight leading a company of soldiers comes to muster all able-bodied people in the area. War is coming.", "The party starts as disparate treasure hunters who have been brought together by a wealthy patron to seek a legendary tome. Little does the party know, the tome is one of unspeakable evil, and their charismatic patron is the leader of a secret organization. An organization bent on destruction/subjugation/apotheosis/something evil.", "A large gathering has brought the party to one town; a festival, a circus, etc. At night, a band of thieves rummages and steal all the expensive (more than common gear) items in the village. All adventurers are hired to find out who did it and get back all the stolen goods.",
        "For reasons of your own, you have each chosen (or been chosen) to join Ye Olde Heroes for Hyre, the largest private security & mercenary company in the land. As the newest recruits you’ve been assigned to the same squad. Your squad leader has just handed you your first assignment.", "A traveling carnival has rolled into town, and you’ve all decided to check out the attractions. A commotion erupts and you realize that you’re all in the path of an escaped (or was it freed?) angry beast. Its trainer is nowhere to be found; you’re the only ones who can stop it from charging into the big top full of helpless townsfolk.", "The party has been executed by way of bottomless pit for crimes real or imagined. However, after hours of falling, they find themselves in a bustling underground City with a citizenry full of “executed” peoples. They must find their way back to the surface for their revenge.", "You arrive at the gates of the reclusive gnome inventor Tinky Tonka’s factory, each carrying the golden ticket that allows you to see what goes on inside. What wonders await!", "Metal men have come out from an old ruined castle and have begun chopping down the forest. They carry the lumber back to the castle, but no one has gotten close enough to see what it’s being used for.", "You are each arrested and pulled into the Chancellory Court. There, a tearful widow declares that it was one of you who killed her husband. When she cannot identify which one, and you each declare your innocence, you are provisionally released while more information is gathered. You can escape the town together, or band together to find the real killer. The DM can also talk privately to one player beforehand, so one of them is actually the killer, and can put that into their character’s backstory.", 
        "You are each contacted by Sowusi Aletongo, a Thayyan Prince who offers you an incredibly lucrative deal. When you arrive at the agreed upon meeting spot, an abandoned cabin in the forest, you find only the other party members.", "You are all (at least) 2nd cousins via the progenitor Golan the Virile, a handsome, dashing and marriage-hopping bard. He arranged a small inheritance for all of his progeny and step-progeny, and had all of them meet in small groups based on geography. The party represents the grandchildren in your area.", "The surrounding areas are all suffering an extreme drought but this town is having a massive harvest festival. Different rumors intrigue each member enough to make a trip.", "Heavy rains have washed out all bridges and paths except the main highway. People have formed a caravan to reach higher ground. Your party has all ended up traveling together.", "A local magistrate has sent his brute squad to round up anyone who looks suspicious. Your party was all gathered up. The magistrate isn’t looking arrest you. He is looking to form a band of strangers to kidnap someone.", "You have all attended a large banquet. During the feast you have all been knocked unconscious. You wake in the morning and all the guests but you have died.", "A huge, Violet hurricane came blowing through the town where all the characters happened to be staying. You are trapped for hours together in a cellar while it blows over.", "Each player owes a favor to a local Lord. A mysterious figure recently came to the Lord and purchased those favors, and now he calls the characters together to collect.", 
        "You are orphan children and you are part of an orphanage that is self-supporting with agriculture. Suddenly, the golem-worker stops working.", "Some noble lord has made a call for adventurers to help him clear the goblin infestation out of his land or complete some other quest appropriate to the campaign. Whoever does the best job will win a prize. However, all of you missed the memo that you were supposed to come in teams of (insert party size here). You’re the only ones who showed up alone, so now you’re stuck on a team together.", "Each of you independently helped stop some crime or disaster just before the campaign began, all for your own reasons. You have all been invited to some ceremony where you will be honored for your participation. During the ceremony, someone who isn’t happy about your deeds attacks, targeting all of you specifically.", "The players are all trying to kill the same reclusive Lord and have planned thier break ins on the same day.", "It’s the day of the year every guild in the capital dreads. Every guild must present their annual accounting report to the department of accounting in order to determine if everything is reported correctly and is in order. Each member of the party has drawn the short straw of delivering the books of their respective guilds, and have spent the last 8 hours standing together in line, getting to know one another to pass the time. As the first of the PCs makes it to the front, a commotion can be heard at the entrance…", "As you fall uncouncious in battle, you hear the god Lathander speak out to Tyr: He is not ready yet, let him go! You wake up from a bird pecking around your clothes. Around you body’s lay on the ground, there are people in the distance. The air smells metallic from the blood around you, the ground is red. You see some others wake up, and your eyes meet. You notice a connection in their eyes. Wounded, what would you like to do.", 
        "You’re startled into wakefulness by the crow of a rooster. You manage to unstick your eyelids, to find that the pressure on your chest was the rooster itself, who glares at you with homicidal malice and crows again. This sets off the monstrous headache you had only just started to understand you had. You slide off the wooden… shelves, covered with straw? that you were sleeping on and stagger to your feet. NOW the headache hits: this is a hangover only in the sense that the plague is an illness. You are in your only set of fine clothes, now ruined: smeared with mud, dung, broken eggs, straw and the thick, pungent smell of the jug of mysterious liquor you now remember that guy in the hat insisting you drink. A couple of hens peck the ground near your feet. You vomit in the dust and straw in front of you: the chickens investigate. You wash out your mouth with the last sip of stale water from your canteen. Now you can see that you were sleeping on the bench of a wagon, which somehow crashed through the wall of the chicken coop. Vanished are the horses you remember pulling the wagon, too fast across a bridge with the rest of the party in the back. The rest of your party is sprawled across the coop and begins to awaken. The rooster crows again. Gods, it hurts.",
        "The party wakes up on a beach in front of a neverending forest that seems to go on for forever, no matter how far they go. They must complete a quest in the woods in order to be allowed to leave.", "Returning home after a long journey, you’re comforted by the familiar sight of the rolling hills that surround the village and begin thinking about a hot bath and your nice, comfortable bed. Your happiness turns to shock as you crest the hill. How can the entire village have disappeared?", "An aging wizard wishes to give back to the community and start his own wizarding school. He urgently needs his first ever spell book, but he sold it some years back while travelling the land. Return it and he’ll pay handsomely.", "The characters are attending a local fair and doing their own things when they hear screaming. The performers at the center of the fair are being attacked. While everyone else rushes away from danger, the heroes put their heads down and charge in.", "Your party has just finished its first job as official adventurers walking back to the woman who gave you your job when she comes running up, claiming the princess has been assassinated and the group is being framed.", 
        "The PC were part of a dungeon exploration group. Almost all other npcs apart from the leader, his mage and three of his soldiers met their demise in those heavily trapped dungeon. Im the last room, some sort of artifact os displayed on an altar, surrounded by mounds of gold and gemstones. While resting, one of the party overhears a conversation between the leader and his mage assistant. They want to get the artifact and gold to themself. During night, whem everyone seems asleep, you see the leader and his aide at the pedestal where the artifact is. On touch, the leader screams and throws the artifact towards the group, to suddenly get ripped apart by an unknown force. A strong wind seems to suck in all that is close and its getting stronger. You wake up everyone, tske the artifact and run out of the dungeon. Outside, the dungeon collapses and you are stranded in the mountains without gear or rations, only one horse with provisons is still there, while all others fled. Its the PCs and the three soldiers survived.", "The party is hired to investigate a death at the archaeological dig site of a ruined city. One of the archaeologists disappeared into thin air, only to reappear days later. He seemed fine at first, but then during supper started rapidly aging and died of old age. While investigating, the party steps through a one-way portal. They find themselves in the city before it was ruined, and later on figure out the entire city is stuck in a Groundhog Day loop. They have to either figure out how to break the city from the loop or find out how to escape themselves while not dying the same way the archaeologist did.", 
        "A strange looking ship without a crew floated into the local docks. The charts inside seem to chart a route to previously unknown lands. In the cabin what is presumably the captain’s journal is found written in a strange language, stained with blood. The journal has been translated, all you need to do is join the crew.", "The party starts out as a group of young nobility at a private academy that their elders sent them to to learn about culture, etiquette, and politics. Unknown to the elders some of the masters were also teaching them how to fight and cast magic. These masters are new arrivals and no one is quite sure how they got their positions. One night when the party is heading to their secret training (which they don’t realize is not part of the normal curriculum) they find their new masters slaughtered and some clues that will lead them on the rest of the adventure. Secretly the masters were a group that con-ed their way into the academy so they could train and recruit the young and powerful for their cause [insert main quest here].", "The party are all at a huge auction: the only problem? They all want the same item.", "Annual hunting competition, the party is thrown together as a band of misfits since the competition requires a certain hunting party size.",
        "A local magistrate puts together a special task force of city guard and ex-cons to take down a city smuggling ring.", "Group is in a seaside tavern when a local fisherman barges in with tales of a terrific sea monster. After calming his nerves, the fisherman wants to hire some adventurers to slay the beast.", "Local tavern has a dance competition. During a particularly vigorous Goliath clog dancing performance, the floor gives way and the party and many bar patrons find themselves in the bottom of a cavern that also happens to be a large goblin settlement.", "The BBEG captures the party after hearing prophecy that only they can stop his evil plan. The party had never met, the BBEG ends up being what introduces the group to each other.", "The party is attending a city’s Carnevale masquerade event. During the festivities in the main square, a group all wearing the same mask start stabbing revelers.", 
        "A strange door leading to a magical endless and random dungeon has appeared in the countryside. It quickly becomes a magnet for adventurer’s and their hanger-ons.", "Party is summoned to famous eccentric wizard’s tower. The wizard is hiring adventurers for….. test subjects.", "During general sewer maintenance, a new series of tunnels are found under the city. The town is hiring folks to explore it.", "A new continent is found across the sea. Adventurers are all travelling across the sea to tame this new wild land.", "In the kingdom that the players were in there was a group of famed hero’s who have saved the land several times, kept the peace, and at this point, provided stability to the monarch since they can influence public opinion. Unfortunately, they all died in their sleep one night at the palace of what seems to be a potent and rare poison. The current monarch, who only recently assumed the throne after their father’s death, fears the people might turn on them so they seek to cover up the heroes’ mysterious demise. The monarch’s wizards believe any magical illusion could be found out by any number of magic users in the city so they use scrying to search the kingdom for look-a-likes. The players have been brought together as they each most resemble one of the fallen heroes. They are only asked to play the part of the kingdom’s legends but end up actually getting caught up in an adventure to protect their homeland and must become the heroes they were pretending to be.", "The party along with a couple of NPCs have been kidnapped by a litch to be used as experiments. They must break out together, but little do they know that the NPCs are being used as spies by the litch.", "The different members of the party have been hired to steal a magic book from the king’s archives. However, touching the book teleports you inside it. The characters must work together and survive to the end of the book’s story or find some other way to escape the book.", 
        "A play about a werewolf is going on in town. It turns out they used a real werewolf as an actor to help with realism in the show, and the theater has hired the PCs to restrain him while acting as werewolf hunters.", "The PCs were given the options of life in prison and the labarynth. Assuming they chose the labarynth, they are teleported to a saw style dragon’s lair and must work together to survive.", "A mermaid gave each of them a magic sword and told them they were the chosen one to kill an evil lord. When the players all reach the Lord’s castle carrying similar swords they figure out that the Lord isn’t that evil and the mermaid just wants someone to kill her ex.", "You all wake up in a burning cornfield.", "The party wakes up to a rumbling as the tower wall of the dungeon they were in collapses into the sea. The time dilation field that held them is now broken as the castle deteriorated. It’s been three centuries since they were imprisoned.", "The party is left to die at the edge of town. Some are tied to stakes, a pillory holds a couple of you, others tied flat to the ground from each limb, and one is shoved into a pauper’s cage. A dog is enjoying licking the bare feet of one prisoner…but there’s something tied to his collar.", "One of the players is a very powerful noble prince who has hired the other characters to guard him as he makes a hunting trip. The hunting trip will be ambushed by a cult that needs the prince’s blood for a demon ritual.",
        "You and your companions are enjoying a quiet ale. A man bursts through the door of the tavern, blood streaming from his eyes, screaming that the sun has turned black and that fireballs are raining down from the skies. Where the fireballs hit, hybrid creatures are emerging from the earth. They are eating the locals.", "A storm-tossed slaver ship at sea. You are chained in the hold, with hundreds of others, laying like books on narrow shelves, when suddenly a repeated series of booms staves in the side of the ship and seawater pours through the breach. One of the cabin boys has hid head stoved in and is lying dead at your feet. The keys to your shackles are on his belt.", "You are awoken from your bed in the middle of the night by your children. Their faces are upside-down and your wife/husband is screaming at the top of their lungs. You can hear your neighbors screaming as well. It feels like morning, but its still black as midnight.", "The King has ordered you to his court, complete with armed escort. When you arrive you are accused of sedition and ordered to the gallows. As the noose tightens and the crowd roars for your blood, you suddenly awaken. Your gut is tight and sour. There is the sound of mailed fists pounding at your door and a voice shouting, 'OPEN UP IN THE NAME OF THE KING!'", "You wake up to boots in your ribs. 'Wake up, maggot! MOVE MOVE MOVE!' All around you there are shouting men amid a darkened room of cots and others like you - torches in their faces, being pushed and herded towards an open door where there is bright light. You have no memory of who you are or how you got here.", 
        "Your bones crack as your back hits the wall of the alley, and you feel the cold steel of a knife pressed against your throat. Your assailant grins at you, feeling around your belt for your coin purse and anything else he can easily remove, and your companions line the wall next to you in similar situations.", "A jury summons appears through your door - the court is in session for a close friend of yours, framed for the murder of his family. He begs you to set him free and find the real culprits.", "You look out of your window to see nothing but pure black darkness - a magical fog has descended upon your home town and made it impossible to see the creatures that lash out of the shadows and attack anything in range.", "In a last-ditch effort to save your life, you drunk a strange fluid offered to you by a priest and you find yourself reborn - in the same place, but in a completely different time period.", "Time has lost all meaning, stuck in this deep, dark pit that you've been thrown into. Your stomach grumbles and whines for food, your body rapidly deteriorating as the days go past. Suddenly, there's a sharp shaft of light coming from the earth above you - 'Don't worry friend, we're here to rescue you.'", "You are the latest brave expedition of the Orgnomian Explorer's Society! Your first journey? To find the previous expedition, of course! Tally Ho!", "A self-described 'rich eccentric' nobleman has hired you to help stock his savage menagerie. He gives you locations of ruins, lairs, and dungeons, and in exchange you bring back specimens (alive or at least mostly intact). His tastes run darker when he makes requests for his 'personal gallery'.", "A town has hired you as additional guards as they put a discovered necromancer on trial for crimes against the townsfolk. She pleads her innocence, and it looks like she's telling the truth.", 
        "Never pass up free rounds at the tavern, right? Tell that to the boat you just woke up on, with the shore nowhere to be seen.", "A world famous band of bards is on tour, and want you to roadie for them. In between gigs, they want to check out your adventures to get material for new songs.", "Members of an assasssins' guild trying to fulfill a high-profile contract.", "Members of a band of outlaws who are ambushing a supply wagons along a winding road to the mines.", "Members of a pirate crew who are about to seize a merchant vessel carrying the king's private requisitions from a far-off land.", "Members of a gang of thieves who have been tasked with robbing the vault of a merchant guild.", "Members of a mercenary company that has been hired to quickly seize a castle while the lord and his knights are away.", "Members of a barbarian clan who are raiding homesteads on the frontier.", "Members of the city watch who have been tasked with patrolling the walls during a siege.", "Captives of a goblin raiding party who are on the run after abducting travelers along the road.", "Captives of a werewolf pack that are hiding in the woods from the king's huntsmen.", "Slaves in a vampire court that is attempting to infiltrate the city's upper social circles.", "Investigators on the trail of a twisted serial killer who has been preying upon prostitutes in the River District.", "Helpers of an evil witch who has tasked them with gathering ingredients and components for foul demonic rituals.", "Members of a strange cult that is plotting to summon a foul presence to the city.", "Fugitives traveling with a merchant caravan trying to escape across the desert.", "Members of a secret society that is plotting to instigate a rebellion among the city's poor districts.", "In the employ of the last scion of a notorious noble house that has repeatedly attempted to assassinate the king and usurp the throne.", 
        "Another day, another beating. You've been in this dungeon for so long that you can't remember how many times you've heard a rib break, or the evil laughter of your captors. But today is different. Today he let his guard down, and you now have the key to your shackles.", "Due to a fire in town, you come home late to find your teenage son standing over your wife's and two daughters' lifeless bodies with a look that you've never seen in the boys eyes. You try to stop him from leaving, but he passes straight through you, dropping the still-wet knife at your feet as a mob he alerted to a multiple murder forms outside your house.", "It's just another night on the wall. Nothing ever happens here, and so no one takes notice when a wizard walks out of town and into the forest. Moments later, a foul rumbling shakes the ground as countless skeletons begin pouring out of the woods towards the town.", "'Just keep the guy safe,' the barkeep had said, 'and you'll come out richer than you could ever hope for.' No one told you that this guy was going on a suicidal vigilante spree and that keeping him safe would be a lot harder than expected.", "The crowd roars as you step into the pit. As the monstrous gate on the opposing wall lifts, the rumble of some foul creature issues forth as a challenge to your authority as a ring champion.", 
        "It was just a game. There's no way those guys were serious about collecting my soul for losing. But if that's so, why do I keep seeing shady, robed people everywhere I go? And now there are three of them headed towards this pub from outside with the air of intent to get what's theirs.", "It's been three days. Those...creatures...aren't going anywhere, and you're out of supplies. The city is desolate except for whatever those things outside your door are, so your only chance is to try to make it out of town. Why'd you decide it was best to hole up in the center of the city?", "You find a salesman offering a beautiful Elven dagger with an intricate inscription that's foreign to you. The weapon is being offered at a staggeringly low price, and since you have always wanted something of this quality, you buy it quickly and take it home, leaving it by your bed. You notice the next morning that you have someone else's blood on your hands and can't seem to let go of the knife. A stern knock accompanied by a loud 'Guards, open up.' instinctively brings the knife to bear, despite your best efforts.", "You are walking along the docks, on the way to a tavern, when a badly damaged construct falls in front of your feet. From it's mouth rolls a scrap of parchment, and you can see writing on it.",
    ]    
    /*Introduction*/
    let plot = 
    [
        [ 'The PCs are seeking shelter from the elements or some other threat, and come across a place to hole up. They find that they have stumbled across something dangerous, secret, or supernatural, and must then deal with it in order to enjoy a little rest.', 'Some bad guys have arrived and done some bad guy things. The PCs were none the wiser. The bad guys have now made good their escape, and the PCs have caught wind of it in time to chase them down before they make it back to their lair, their home nation, behind enemy lines, etc.', "Usually through trickery (but sometimes by digging into the PCs' past), an antagonist has something to hold over the heads of the PCs and make them jump. This could be any kind of threat from physical to social, but it depends on the villain having something - even if it's information - that others don't have. Now, he is pulling the strings of the PCs, telling them to do things they don't want to. The PCs must end the cycle of blackmail, deprive the villain of his edge, and keep him temporarily satisfied while doing it.", `Mission objective: enter the dangerous place, and retrieve the vital dingus or valuable person. Overcome the area's defenses to do so.`, 'The PCs must secure a target for the good guys. There are bad guys there that prefer not to be secured. The fundamental tactical scenario.', 'There is a place where bad things live. The PCs must make it safe for nice people, systematically clearing it of danger.', 
        "The PCs are treasure-hunters, who have caught wind of a treasure-laden ruin. They go to explore it, and must deal with its supernatural denizens to win the treasure and get out alive.", 'The PCs are stranded in a strange place and they must survive by finding food and shelter, and then worry about getting back home.', "A crime or atrocity has been committed, the PCs must solve it. They must interview witnesses (and prevent them from being killed), gather clues (and prevent them from being stolen or ruined). They must then assemble proof to deliver to the authorities, or serve as personal ministers of justice.", 'The PCs have a valuable object or person, which needs to be taken to a safe place or to its rightful owner, etc. They must undertake a dangerous journey in which one or more factions (and chance and misfortune) try to deprive them of the thing in their care.', 'The PCs are placed in charge of a large operation (a trading company, a feudal barony, the CIA). Despite a lack of experience in such things, they must make it work and thrive.', "A person (church group, nation, galaxy) is in a hazardous situation they can't survive without rescue.The PCs are on the job. In some scenarios, the hook is as simple as a distant yell or crackly distress signal.", "The PCs, while traveling or exploring, come across a hornet's nest of bad guys, preparing for Big Badness. They must either find some way to get word to the good guys, or sneak in and disable the place themselves, or a combination of both.", "Within a defined area, something important and valuable exists. The PCs (or their employers) want it, but so do one or more other groups. The ones that get it will be the ones that can outthink and outrace the others, deal best with the natives of the area, and learn the most about their target. Each competing group has its own agenda and resources.", 
        "The PCs are minding their own business when they are attacked or threatened. They don't know why. They must solve the mystery of their attacker's motives, and in the meantime fend off more attacks. They must put two and two together to deal with the problem.", "The PCs are a diplomatic vanguard, trying to open up (or shore up) either political or trade relations with a strange culture. All they have to do is manage for a day or so among the strange customs without offending anybody . . . and what information they have is both incomplete and dangerously misleading.", "The PCs are working surveillance - spying on a person, gathering information on a beast in the wild, scouting a new sector. Regardless of the scale, the primary conflict (at least at the start) is the rule that they are only to watch, listen and learn. They are not to make contact or let themselves be known.", "Someone is gone: they've run away, gotten lost, or simply haven't called home in a while. Somebody misses them or needs them returned. The PCs are called in to find them and bring them back.", "One or more of the PCs wakes up with no memory of the recent past, and now they find themselves in some kind of trouble they don't understand. The PCs must find the reason for the memory lapse, and solve any problems they uncover in the meantime.", "Something both bad and inexplicable is happening (racial tension is being fired up in town, all the power is out, the beer supply is drained, it's snowing in July, Voyager still has fans, hordes of aliens are eating all the cheese), and a lot of people are very troubled by it. The PCs must track the phenomenon to its source, and stop it.", "The PCs are assigned to guard a single vital spot (anything from a mountain pass to a solar system) from impending or possible attack. They must plan their defensive strategy, set up watches, set traps, and so on, and then deal with the enemy when it arrives.", 
        "The PCs are minding their own business and find themselves transported to a strange place. They must figure out where they are, why they are there and how to escape.", "A villain or organization is getting ready to do something bad, and the PCs have received a tip-off of some sort. They must investigate to find out more about the caper, and then act to prevent it.", "Somebody has tinkered with Things Man Ought Not, or opened a portal to the Mean People Dimension, cracked a wall at the state prison, or summoned an ancient Babylonian god into a penthouse. Before the PCs can even think of confronting the source of the trouble, they must deal with the waves of trouble already released by it: monsters, old foes out for vengeance, curious aliens who think cars/citizens/McDonald's hamburgers resemble food, and so forth.", "Somebody needs a dingus (to fulfill a prophecy, heal the monarch, prevent a war, cure a disease, or what have you). The PCs must find a dingus. Often an old dingus, a mysterious dingus, and a powerful dingus. The PCs must learn more about it to track it down, and then deal with taking it from wherever it is.", "A town, castle, starship, outpost, or other civilized construct is lying in ruins. Very recently, it was just dandy. The PCs must enter the ruins, explore them, and find out what happened.", `The PCs must travel through a hazardous area, and get through without being killed, robbed, humiliated, debased, diseased, or educated by whatever is there. The troubles they encounter are rarely personal in nature - the place itself is the "villain" of the adventure.`, 
        "The PCs are on a hunting expedition, to capture or kill an elusive and prized creature. They must deal with its environment, its own ability to evade them, and possibly its ability to fight them.", 'The PCs are participants in a race, contest, tournament, scavenger hunt or other voluntary bit of sport. They must win.', 'The PCs are imprisoned, and must engineer an escape, overcoming any guards, automatic measures, and geographic isolation their prison imposes on them.', 'The PCs are on board a populated conveyance (East Indiaman, Cruise Ship, Ferry, Sleeper Starship), when it is hijacked. The PCs must take action while the normals sit and twiddle.', "A bad guy (or a group of them, or multiple parties) is kicking up a ruckus, upsetting the neighbors, poisoning the reservoirs, or otherwise causing trouble. The PCs have to go where the trouble is, locate the bad guys, and stop the party.", "The PCs are explorers, and their goal is to enter an unknown territory and scope it out. Naturally, the job isn't just going to be surveying and drawing sketches of local fauna, something is there, something fascinating and threatening.", `Any of the basic plots in this list can be re-engineered with the PCs on the outside of it. Either the PCs are accompanying other characters in the midst of such a plot (often being called on to defend the plot from the outside, as it were), or they are minding their own business when the others involved in the plot show up, and must pick sides or simply resist. For instance, with Any Old Port In The Storm, the PCs could already be enjoying (or native to) the shelter when a strange group arrives. If the "the PCs are unwelcome" variant is employed, then perhaps the PCs will be the only voice of reason to still the religious fervor, racial prejudice, anti-monster sentiment, or whatever else is the source of conflict. The PCs find themselves on the receiving end of the adventure. Take any of the plots here and reverse them, placing the PCs in the position where NPCs (often the villain, fugitive, et cetera) normally are`, ],
        [ "Quest (Act 1: forcing action 'what is the problem?', Act 2: building towards the goal, Act 3: revelation of the solution to the problem)", "Adventure (First movement: Abrupt leaving of home/ routine and the core purpose of the hero, Second movement: relevant choices being made along the journey, Third movement: fulfillment of the promises made during the first two movements)", "Pursuit (First movement: Identify of the two players, one chases and one is chased, and the why for the chase and the starting event, Second Movement: Chase -- twist, turns, reversals, Third movement: resolving the chase, either escape or capture)", "Rescue (Act 1: Separation - protagonist identified, antagonist identified, victim identified and the motivation for rescue is clear. Act 2: Pursuit and surprises. Act 3: Confrontation between protagonist and antagonist.)", "Escape (Act 1: Imprisonment. Act 2: planning, failed escape attempt, Act 3: the successful escape)", "Revenge (First movement: the crime, start the scene late and get out early, Second movement: the planning and building towards revenge, Third movement: confrontation)", "The Riddle/ Mystery (Two element structure (general/ metaphorical, specific/paradoxical, clues and references. Act 1: Exploring the general/ metaphrical aspects, Act 2: Exploring the particulars, Act 3: Solving the riddle", "Rivalry (Protagonist vs antagonist on roughly equal footing in a tug of war, an immovable object meets an irresistable force struggling for power over the other)", "Underdog (Unmached strengths between protagoinst and antagonist. Act 1: Antagonists have the upperhand, protagonist is disempowered. Act 2: protagonist empowered and ready to stand toe to toe with the antagonist but fails in establishing their hold. Act 3: The rising power of the antagonist and the final victory)", "Temptation (First movement: temptation established and the protagonist succumbs to it, Second movement: undergoing effects based on the decision, denial and finding a way out of punishments to follow. Third movement: rise and resolution of the internal conflicts, the crisis has been forced.)", 
        "Metamorphasis (Act 1: the curse is introduced and the antagonist has felt the affects of it. Act 2: the relationship between the metamorph and the antagonist. Act 3: terms of release from the curse and resolution)", "Transformation (Act 1: An incident that changes the protagonist's life, Act 2: The full transfomative effects are seen, Act 3: the incident that defines the results of the transformation)", "Maturation (Coming of age, Act 1: 'before' and a catalytic event Act 2: rejecting the demands of the event, Act 3: developing newfound maturity and the verificaiton of this new maturity.)", "Love (Act 1: Lovers found, Act 2: Lovers split, Act 3: Lovers Reunited)", "Sacrifice (Act 1: Identifying ideals, Act 2: moral dillema with no easy solution forcing a hard choice, Act 3: making the choice and the cost of the sacrifice)", "Discovery (Act 1: the catalyst to a journey, start the scene late and get out early, Act 2: meaningful internal crisis, Act 3: resolution of the crisis)", "Wretched Excess (Act 1: life as normal, a catalyst Act 2: loss of control towards some obsession, Act 3: crisis point and the result )", "Ascension and Descension (Positive or a negative reactions under stress. Act 1: identifying the main character and the circumstances they start in, Act 2: moral stresses and the decisions that are propelling the character into their new self, Act 3: culmination of character and events)", ]
    ]
    /*Development*/
    let devs= [
        ['The shelter contains the cause of the threat the PCs were trying to avoid.', `The shelter houses a ${'Hidden Base'}.`, 'The PCs must not only struggle for shelter, they must struggle to survive.', 'The place is a legitimate shelter of some kind, but the PCs are not welcome, and must win hearts or minds to earn their bed for the night.'], 
        [ 'The bad guys escaped by stealing a conveyance that the PCs know better than they do.', 'The bad guys duck down a metaphorical (or literal) side-road, trying to hide or blend into an environment (often one hostile to the PCs).', `If the bad guys cross the adventure's "finish line" (cross the county line, make the warp jump, etc.) there's no way to pursue them beyond it.`, ], [ 'The adventure hook involves the PCs doing the villain a good turn, which allows him to take advantage of them (very cynical!).', 'To succeed, the PCs must contact other folks that are also being used.', "The PCs aren't the victims at all, but somebody they care about/are charged to protect, is.", ], [ 'The goal is not to extract a thing, but to destroy a thing or interfere with a process (kill the force-screen generator, assassinate the evil king, stop the spell from being cast, wreck the invasion plans, close the portal).', 'The goal has moved.', 'The goal is information, which must be broadcast or otherwise released from the area as soon as it is found.', 'The job must be done without alerting anyone.', "The PCs don't know the place is dangerous.", 'The PCs must replace the thing with another thing.', ], [ "The PCs must assemble and/or train a force to do the job with them.", "The PCs are working with flawed intelligence and the target zone isn't as described.", "The PCs must coordinate their own efforts with an ally group (possibly putting aside rivalries to do so).", "The target zone includes a population of innocent people, fragile goods, or some other precious thing that mustn't be harmed in the crossfire.", ], 
        [ "The bad things can't be beaten with direct conflict.", 'The PCs must learn more about the inhabitants to solve the problem.', "The Haunted House; The Alien Infestation; The Wild Forest.", ], [ "The treasure itself is something dangerous.", `The treasure isn't in a ruin, but in a wilderness or even hidden somewhere "civilized."`, "The treasure is someone else's rightful property.", "The treasure turns out to have a will of its own.", ], [ "The PCs must survive only for a short period of time, until help arrives, the ship and/or radio is repaired, or some such thing", `in "repair" scenarios, sometimes the PCs must discover some fact about the local environment that will make such repairs possible.` ], [ "The PCs are working to clear an innocent already accused (possibly themselves).", "The PCs must work alongside a special investigator or are otherwise saddled with an unwanted ally.", 'Midway through the adventure, the PCs are "taken off the case" - their invitation/authority to pursue the matter is closed (often the result of political maneuvering by an antagonist).', 'The climax is a courtroom scene or other arena of judgment.', "The scale is highly variable for this type of adventure, from a small-town murder to a planetwide pollution scandal.", ], 
        [ 'The thing or person is troublesome, and tries to escape or sidetrack the PCs.', "The destination has been destroyed or suborned by the enemy, and the PCs must take upon themselves the job that either the destination or their charge was meant to do when it got there.", 'The person is attempting a political defection.', "Safe arrival at the destination doesn't end the story; the PCs must then bargain with their charge as their token (exchanging money for a hostage, for instance).", 'The PCs must protect the target without the target knowing about it.', ], [ 'The PCs are brought in because something big is about to happen, and the Old Guard wants a chance to escape.', 'The peasants, neighbors, employees, et cetera resent the PCs, because their method of inheritance looks outwardly bad and everybody loved the old boss. ', ], [ 'The victim(s) is (are) a hostage, or under siege from enemy forces, and the PCs must deal with the captors or break the siege.', 'There is a danger that any rescue attempts will strand the rescuers in the same soup as the rescuees, compounding the problem.', "The rescuees aren't people, but animals, robots, or something else.", `The "victim" doesn't realize that he needs rescuing; he thinks he's doing something reasonable and/or safe.`, "The threat isn't villain-oriented at all; it's a natural disaster, nuclear meltdown, or disease outbreak.", "The rescuees can't leave ; something immobile and vital must be tended to or dealt with at the adventure location.", 'The PCs begin as part of the rescuees, and must escape and gather forces or resources to bring back and proceed as above.', ], ['The PCs must figure out how to use local resources in order to defend themselves or have a chance against the inhabitants.'], 
        [ 'The natives require the competing factions to gather before them as pals to state their cases.', 'The valuable thing was en route somewhere when its conveyance or courier wrecked or vanished.' ], [ "The PCs have something that the bad guys want - but they don't necessarily realize it.", "The bad guys are out for revenge for a dead compatriot from a previous adventure.", "The bad guys have mistaken the PCs for somebody else.", ], ["The PCs were chosen by somebody who knew they weren't prepared for it - an NPC trying to sabotage the works (pinning this villain might be necessary to avert disaster)."], ["The target gets itself in trouble and the PCs must decide whether to break the no-contact rule in order to mount a rescue.","The target is about to commit a heinous crime; will the PCs interfere or obey orders?"], [ "The target has been kidnapped (possibly to specifically lure the PCs).", "The target is dangerous and escaped from a facility designed to protect the public.", "The target is valuable and escaped from a place designed to keep him safe, cozy, and conveniently handy.", "The target has a reason for leaving that the PCs will sympathize with.", "The target has stumbled across another adventure (either as protagonist or victim), which the PCs must then undertake themselves.", `The missing "person" is an entire expedition or pilgrimage of some kind.`, `The target isn't a runaway or missing/lost - they're just someone that the PCs have been hired to track down (possibly under false pretenses).`, ], 
        [ `The forgetful PCs voluntarily suppressed or erased the memories, and they find themselves undoing their own work.`, `The entire party is temporarily amnesiac.`, `The PCs drank too much alcohol last night, and now have a massive hangover.`, ], [ `The PCs are somehow unwittingly responsible for the whole thing.`, `What seems to be a problem of one nature (technological, personal, biological, chemical, magical, political, etc) is actually a problem of an alternate one.`, ], [ `The intelligence the PCs was given turns out to be faulty, but acting on the new information could result in greater danger - but so could not acting on it, and the PCs must choose or create a compromise.`, `The PCs learn that the enemy has good and sympathetic reason for wanting to destroy the protected spot.`, ], [ `They were brought there specifically to help someone in trouble.`, `They were brought there by accident, as a by-product of something strange and secret. Some of the PCs' enemies were transported along with them (or separately), and now they have a new battleground, and innocents to convince which guys are the good guys.`, ], 
        [ `The initial tip-off was a red herring meant to distract the PCs from the actual caper.`, `There are two simultaneous Bad Things on the way, and no apparent way to prevent both of them - how to choose?`, ], [ `The PCs can't simply take the released badness to the mat; they have to collect it and shove it back into the source before it the adventure can really end.`, `The PCs are drawn in to the source and must solve problems on the other side before returning to this one.`, `A secret book, code, or other rare element is necessary to plug the breach (maybe just the fellow who opened it).`, `A close cousin to this plot is the basic "somebody has traveled into the past and messed with our reality" story.`, ], [ `The dingus is incomplete when found (one of the most irritating and un-fun plot twists in the universe).`, `Somebody already owns it (or recently stole it, sometimes with legitimate claim or cause).`, `The dingus is information, or an idea, or a substance, not a specific dingus.`, `The PCs must "go undercover" or otherwise infiltrate a group or society, gaining the dingus by guile or stealth.`, ], [ `Whatever ruined the ruins (including mean people, weird radiation, monsters, a new race, ghosts) is still a threat; the PCs must save the day.`, `The inhabitants destroyed themselves.`, `The "ruins" are a derelict ship or spaceship, recently discovered.`, `The "ruin" is a ghost town, stumbled across as the PCs travel - but the map says the town is alive and well.`, `Alternatively, a town recently destroyed by some monster or cataclysm suddenly reappears completely unharmed, and the PCs must discover the mystery of its resurrection, possibly re-destroying it in the process.`, ], [ `The place isn't dangerous at all, and the various "dangers" are actually attempts to communicate with the party by some agent or another.`,], [ `The creature is immune to their devices and weapons.`, `There are other people actively protecting the creature.`, `The creature's lair allows the PCs to stumble onto another adventure.`, ], 
        [ `The other contestants are less honest, and the PCs must overcome their attempts to win dishonestly.`, `The PCs are competing for a deeper purpose than victory, such as to keep another contestant safe, or spy on one, or just to get into the place where the event goes down.`, `The PCs don't wish to win; they just wish to prevent the villain from winning.`, `The event is a deliberate test of the PCs abilities (for entry into an organization, for example).`, `The event becomes more deadly than it's supposed to.`, ], [ `Something has happened in the outside world and the prison security has fallen lax because of it.`, `The PCs have been hired to "test" the prison - they aren't normal inmates.`, `Other prisoners decide to blow the whistle for spite or revenge.`, `The PCs are undercover to spy on a prisoner, but are then mistaken for real inmates and kept incarcerated.`, `The PCs must escape on a tight schedule to get to another adventure outside the walls.`, ], [ `The "hijackers" are government agents pulling a complicated caper, forcing the PCs to choose sides.`, `The hijackers don't realize there is a secondary danger that must be dealt with, and any attempt to convince them is viewed as a trick.`, `The normals are unhelpful or even hostile to the PCs because they think the PCs are just making matters worse.`, ], [ `The PCs must not harm the perpetrator(s); they must be bagged alive and well.`, `The bad guys have prepared something dangerous and hidden as "insurance" if they are captured.`, `The "bad guy" is a monster or dangerous animal (or an intelligent creature that everybody thinks is a monster or animal).`, `The "bad guy" is a respected public figure, superior officer, or someone else abusing their authority, and the PCs might meet hostility from normally-helpful quarters who don't accept that the bad guy is bad.`, `A balance of power perpetuates the trouble, and the PCs must choose sides to tip the balance and fix things.`, `The "trouble" is diplomatic or political, and the PCs must make peace, not war.`, ],
        [ `Either the place itself is threatening (in which case the PCs must both play National Geographic and simultaneously try to escape with their skin, sanity, and credit rating) or the place itself is very valuable and wonderful, and something else there is keen on making sure the PCs don't let anyone else know.`, `Other potential conflicts involve damage to the PCs' conveyance or communication equipment, in which case this becomes Don't Eat the Purple Ones. (see above)`, ], 
        [ `Instead of hunting, they must be hunted.`, `Instead of fixing, they must avoid getting "fixed" themselves (ow).`, `Alternately, leave a classic plot intact but turn the twists upside down, making them twistier (or refreshingly un twisty).`, ], [ `The PCs must work alongside an NPC or organization they'd rather not pal around with (those who are normally rivals or villains, or just a snooty expert sent along to "help" them, etc).`, `The victims are really villains and the villains are really victims.`, `The PCs meet others who can help them, but won't unless the PCs agree to help them with their own causes.`, `The villain is somebody the PCs know personally, even respect or love (or someone they fall for, mid-story).`, `The PCs must succeed without violence, or with special discretion.`, `The PCs must succeed without access to powers, equipment, or other resources they're used to having.`, `The villain is a recurring foil.`, `Another group comparable to the PCs has already failed to succeed, and their bodies/equipment/etc provide clues to help the PCs do better.`, `There are innocents nearby that the PCs must keep safe while dealing with the adventure.`, `The adventure begins suddenly and without warning or buildup; the PCs are tossed into the fire of action in scene one.`, `The PCs must pretend to be someone else, or pretend to be themselves but with very different allegiances, values or tastes.`, `The PCs can't do everything and must choose: which evil to thwart? Which innocents to rescue? Which value or ideal to uphold?`, `The PCs must make a personal sacrifice or others will suffer.`, `The PCs aren't asked to solve the problem, just to render aid against a backdrop of larger trouble: get in a shipment of supplies, sneak out a patient that needs medical help, or so on.`, `One of the PCs is (or is presumed to be) a lost heir, fulfillment of a prophecy, a volcano god, or some other savior and/or patsy, which is why the PCs must do whatever the adventure is about.`, `There is another group of PC-like characters "competing" on the same adventure, possibly with very different goals for the outcome.`, ],
    ]

    let storyArray = []
    let chance = rollDice(100)
    if (chance < 50){
        let randNum = Math.floor(Math.random()*plot.length)
        let linkedDev = ""
        if (devs[randNum].length > 2){
            linkedDev = searchArray(devs[randNum]) 
        } else if (devs[randNum]) {
            linkedDev = searchArray(devs[34]) 
        } else {
            let combArray = devs[randNum].concat(devs[34])
            linkedDev = searchArray(combArray) 
        }
        storyArray = `${plot[0][randNum]} ${linkedDev}`
    } else {
        storyArray = `${searchArray(plot[1])} ${searchArray(devs[34])}`
    }  

    let outcomes = [searchArray(startOfConflict), searchArray(startOfConflict), storyArray]    

    document.getElementById("Start").innerHTML = searchArray(outcomes);
};

function conflict() {
    let conflictArray = [
        "Players vs. An Antagonist", "Players vs. Natural Forces", "Players vs. Supernatural Forces", "Players vs. Authority or Tradition",
    ]
    document.getElementById("Conflict").innerHTML = searchArray(conflictArray);
};

function villain() {
    let villainArray = [
        "Monster (Goblins, Oozes, etc.)", "Humanoid (The common races)", "Extraplanar (Elementals, Fey, etc.)", "Dragon (Chromatic, Metallic, etc.)", "Beast (Lions, Tigers, Owlbears, etc.)", "Fiends (Demons, Devils, Yugoloths, etc.)", "Undead (Vampires, Liches, Specters, etc.)", "Aberration (Beholders, Mind Flayers, etc.)",
    ]
    document.getElementById("Villain").innerHTML = searchArray(villainArray);
};

function preMadeVillain() {
    let premades = [
        "Fenston Hill: If the fantasy world had white collar workers, Fenston Hill would be a white collar criminal. He pours gold into having 'fun' with others, liking to manipulate then ruin them. He does all this behind a large unsettling smile and big round eyes.", "Robert Rotten: This Bard uses illusion and disguise to sneak around and train new villains", "Capricorn: Turned from a sheep into a human-like form by a god of destruction, this warlock/wizard is devoted to summoning her at whatever cost.", "Lorraine Caden: After living a childhood full of mockery for her demonic ancestry, Lorraine now seeks redemption through following her god's commands. However, this god believes that cleansing the world is the best course of action.", "Lyra Magnus: An unimpressive bard, but she will do her best to become the most famous musician in the land (mainly by killing or maiming anyone else vying for the top spot).", "Xanathan the Great: Magician by day, Necromancer by night, Xanathan uses his magic show as a front to find people to use for his necromantic experiments.", "Javios Bocoll: warlock son of cultist nobles he lived a life of luxury and pleasures, he learned the dark arts from a very young age, he will do anything from whisper sweet words in your hear to a put a knife on your back to keep his lifestyle of luxury and darkness. He will stay in the shadows while the others destroy each others and will only get his hands dirty if it's extremely neccesary.", 
        "Dom Flamingo: Swash Buckler of the great sea, uses his birds to gain intel on travelers before raiding their ships, very flamboyant for a pirate.  Never caught in the act and always sells stolen goods on black market, he can be found in shady places in large cities.", "Lich Biscuit: A group of evil bards looking to travel the world performing and gaining fame. They wish to become famous enough that they play a show that has a big enough audience that when they play, they perform a ritual that kills the entire audience and summons the avatar of a death god that will reek havock on the world.", "Arrak: A Githyanki Blacksmith, who is trying to make a Silver Sword. He currently believes he needs souls to do so.", "Kate the Cannibal: She loves some good meat.She's strict follower of the law but when the law can't see her she feeds.", "Aguir Reggrim: Once a  honorable clan leader, he started to hear the voice of the wild ones in his head, that led him and his people to worship the wild ones, a pantheon of feral gods and made him succumb into madness and savagery.", "Grey: The Death Cleric : Grey only values 2 things in life: his Blue Book and justice. The Blue Book is a magical item—possessing the soul of a powerful planar entity. The ability the Book possesses is that of bestowing and divination. Whoever is bound to the book receives special abilities and attributes dependent upon their desire. Grey became charming and musical. The book also has the ability to track anyone whose name is written within its pages. It must be the full name. The wielder is able to call upon the entity inside the book to track one of the names written.", "The tracking isn’t completely accurate—the more current of location desired, the less accurate it is. It can only track back, nearly to the exact location (within 500ft), two weeks.", "The pact between Grey and the Blue Book is simple—he kills the unjust whose names appear in the book and the souls of those killed are devoured by the entity in the Blue Book. Any creature whose soul is thus devoured cannot be resurrected until the book returns the soul or is destroyed.",
        "The Book temporarily becomes stronger after every soul devoured—for a maximum of 5 days. In this time, the tracking is more exact and Alder’s magic is amplified.", "Jeremy: Dressed in a massive coat, big enough to fit two gnome whores for travel, Jeremy is a fastidious pimp. He will always try and make money in any way he can. Whores just happened to be the easiest avenue at the time.", "Olldenram Ainkratz: An eldritch knight fighter who came upon a seemingly unidentifiable rune at one point purchased from a traveling group of gypsies. He would spend his whole life devoted to it, and it's applications that would be revealed to him as a mark of the deep ones designed to be engraved into the skin. It saps away WIS and sanity but greatly boosts INT and STR. An additional side effect was that it would cause the user to crave deeper and more inscriptions of other runes into the skin until they reach the bone. Ollendram could barely replicate the original rune, and was almost wholly futile in attempts to create any working variation from his own attempts. No longer satisfied with his i re-create the runes, he now seeks them by slaughtering any and all passing caravans of traders in unsanctimonious prayers that they might have another one, with the idea that it might allow him to ascend his mortal coil and travel to a higher plane of existence to see and feel what lies beyond the realms that can be detected", "Logan Piercer: A former knight of a rich trading country he turned to the side of evil when he was kidnapped and brainwashed. He uses bardic magic that he disguises as holy magic to show that he has the suppourt of the gods.", "Lord Golomor: A beloved lord of his land, starts kidnapping children in order to train them as a specialist military unit to fight another very dangerous country, but in this process the children lose their emotions to become the perfect soldiers. All for the sake of saving his land by this enemy", 
        "Yorn the Elder: At a young age Yorn was initiated into a powerful underground group of magic users called Takhisis' Legion.  Bent on world domination by way of replacing current leaders with people from their organization, eventually Yorn quickly became obsessed with trying to help achieve this goal.  Later on Yorn found his niche in the Legion, after it was discovered that he had an innate gift for mental domination, Yorn was then swept off into seemingly endless training to hone his skills the strongest of which were mental domination and memory replacement.  Now an old man, Yorn spends his time ruling over a small village (possibly a PC's home town if you want) of people who are convinced they are farmers/fishermen but are actually the true rulers of the world's kingoms, people too important to kill and to dangerous to dangerous to let run free.", "Lord Merritt: Once a proud Lord with a fierce round table of 8.  They set out to find the most powerful weapons.  After returning with a glowing sword; Merritt was paranoid and slayed his loyal subjects and has now ruled his castle with an ironfist.  Rumor has it the sword now controls Merritt and feeds off of Merritt's paranoia.", "Lord Tittlewinks: A fey Lord who is conquering lands 1 by one because he is tired of his name getting mocked", 
        "Lord Crosby: An innocent looking journalist, who constantly swigs from a small iron flask, muttering a single passphrase everytime. Crosby is essentially an underground gatherer of information. He will seek to endear himself to the party by telling them of eventual quests they can undertake, but will in reality gather as much information about the PCs as possible. If the players let him, he will even create stunning and very flattering sketches of the party (copies of these sketches will then start to appear everywhere under various headlines). Once he has pumped the team for information he will direct them to a dangerous place (letting the party know that it is totally safe) before sneaking off. After this he will frame our heroes for various crimes, set up loans in their names, change their mailing adress and pretty much be a very annoying identity thief. Lord Crosby does all this to keep up his extravagant lifestyle, but as a result of all his meddeling he has become rather paranoid (that is why he constantly drinks from his magic flask which continously fills with an essential antidote). After Lord Crosby feels that he has gotten enough money out of the party he will forge bounty posters for them and thus hope to have them killed. Crosby is a non-combatant with the commoner stats, he will however have guards.",
        "The Flute Tooter: Once a regular bard, Rene Saunders was stuck by the foul curse of ‘The Flute Salad’. Now taking the forbidden title of ‘The Flute Tooter’, they will stop at nothing until the whole nation follows them in their demented, flute driven dance.", "Broxigar: An old half-orc mercenary captain who attempts to assist with a party's quest randomly. After assisting the party with handling a quest boss/encounter, he will try to take all the loot from the quest, but will eventually settle for little to no reward. Afterward, he and his company will stealthily follow the party to the nearest town and will have one of his companions attempt to assasinate one of the PCs when they are on their own. If left alone, Broxigar will continue to harrass the party in an effort to kill them and steal their loot.", "Cybtar Haregrass: An elven archdruid who is displeased with the way that civilization is 'trespassing' on the frontier. Cybtar will attempt to gather groups of elementals and beasts to destroy any humanoids that are outside their 'natural habitat'. Cybtar does not see himself as evil, rather he finds all who do not respect nature to be criminals whom he must punish for their transgressions. After defeating particularly strong foes, Cybtar will offer them a glade of their own in this new realm (as he sees their strenght as a right to exist in the wild). However, he has no patience for weaklings, who he culls mercilessly.", 
        "Nyol Adorg: The loveliest, sweetest Warlock there is; a childhood friend of one of the PCs.", "Only the fiend has taken over their body, and is just pretending to be Noyl - whose soul has long since been consumed/lost/destroyed/whatever. The fiend's goal is to raze/subjugate a kingdom in order to prove it deserves a higher place in the fiendish hierarchy.", "Orien Markfoot: An old gnome who runs a jewelry shop in a small port town (Riversmouth). He has ties with the thieve's guild but has since gone rogue, and sends the PCs on a simple mission (fetch quest or something harmless) for him. Upon returning the first time he gives a proper reward, to lure them into a false sense of safety. Eventually he sends them on another mission, but this time with a darker purpose. Orien is secretly a very powerful necromancer seeking Lichdom, and basically spends his weekends killing off a small town in order to gather souls to ascend as well as to build his army of undead. The party's second mission, or just hearsay from around the world, takes them to the ghost town where's set up his army. Undead hijinks ensue. Chase him down before he makes it to his ritual chamber, or fight a full powered lich!.", "Baron Markaz: Tiefling Warlock/Necromancer", "Grey-blue skin like decaying whale flesh, mottled with necrotic black. Horns are bone without any flesh. Tail writhes and flails madly and is suckered like an octopus tentacle, and the last sucker has a razor-sharp beak in it.", 
        "His necromancy is not learned - rather, granted by his patron, an eldritch horror from the depths. His preferred necromantic minions are crustaceans leaking blood-red goo and tentacles constructed from bone.", "Kistal Brank: A bartender who cuts you off after only 1 drink. He then urges you to leave and never come back. When the PCs attempt to leave, he calls out sarcastically HEY THANKS FOR THE TIP, DICKHEAD!. Strangely, he does this continually to every custormer, but the bar is still open. In reality, Kistal was a jerk bartender who got cursed by a former patron. Kistal now relives his bad behaviour eternally. If freed from his curse, the former bartender transforms into a demon of your choice, since his own hatred has transformed his real form.", "Pock: A gnome trickery cleric whose mind has been ravaged by the god of madness. Using healing and melting foes with inflict wounds, he wins over the hearts of adventures but his only primary motivation in life is causing chaos upon friends. Using illusions and disguises, anybody involved with Pock eventually meets their end to their friends or becomes part of Pock's cult. His mindcontrol spell is a 2 minute touch spell which causes invisible swirling smoke to engulf the target and enter their mind, causing Pock to become their deity. The DM has a choice to involve his faithful cult following or not. However a powerful plot would be Pock being the ringmaster behind town problems while infiltrating/helping the group to keep an eye on the only peole that could stand in their way.",
        "Duc Jean de Bourgogne: An arrogant, arbitrary, cynical, and cruel former duke who was banished from his domains by his liege for many different insulting acts and offenses, but due to his immense wealth he was able to mass a private army of mercenaries and rebuild his power base as an influential crime lord, with his crime syndicate spanning many realms and kingdoms. His son(s) serve as his lieutenants, and his brother, Baudouin, the Prince of Blood, serves as his second-in-command.", "Polonius the Wise: Polonius is an ageless mage who has seen the world cycle between ruled by the forces of good and evil throughout the ages, he sees the party as disrupting the balance after they saved the world from the last threat and takes it upon himself to maintain balance and kill the party to allow evil to rule once more.", "Hell's Generals: Not much to say aside from just... check out the module and pick one!", 
        "Jimmy : A young boy who feigns innocence all the while stealing, and tripping people all over the city. Jimmy is the kind of child who will go out of his way just to annoy anyone who is younger, slower, dumber or better looking than he is. His primary objective is to be an annoying brat, and to hide his BBEG nature. Following this little jerk, are numerous other street urchin scum who, in swarms, can be quite dangerous, based on just numbers. If threathened, Jimmy will cower and run to get his alcoholic father Jimmy Sr., a veteran fighter who will whip Jimmys enemies, and then give Jimmy a good asswhooping afterwards, you know, just for bothering his old man.", "Boingo The Clown: Leader of a great nomadic tribe of clowns, Boingo is a grizzled old fighter. He sees over his barbarian breatherins lives as they conduct raids and ambushes using their love of comedy and crushing strength", "Rubick: A powerful evil mage that has been performing awful acts and demanding people submit to them. When the players reach them it is revealed to be a child with an illusion spell making them appear as an adult. They are wearing a ring that contains an evil wizard's soul which can communicate with its wearer and cast some of their lower level spells through them. It has been threatening the child into acting on their behalf to acquire slaves that can be used to help them unearth an artifact that will allow the wizard to return to their body.", "Posidea: A corrupt noble who controls water elementals and lords over a desert tribe whose livelihoods depend on the water she conjurs. The townspeople, although good folk, will defend her to the death.", "Sir Stroh: a dwarf who defeated evil wizard, but got disfigured in the fight, wore bandages to cover disfigurement but still got shunned by scared folk, retreated in the abandoned tower where wizard used to live and got mad from the evil wizard's lingering ghost in the tower. Now he uses his combat prowess to find a way to bring wizard back to life.", 
        "Velen Keleda, The Traveller: An elf who made a bargain with the Dark Six (or in your universe whatever evil pantheon or evil god you have) to become a deity. In exchange for becoming an immortal elven shapeshifter, the Dark Six (again whichever evil god(s) you want) tasked him with using his shapeshifting abilities to travel the mortal world and spread their 'holy word'. Only when the god(s) gain enough followers to be able to take over the mortal world will the Traveller be given full deity status.", "Iscah (female kobald), Jacme (male kobald), Yowl (leopard): A pair of immortal kobolds and their pet leopard. They have a tendency to become obsessed with stalking particular targets. When not the minions of a more intelligent being, the trio enact their own selfish plots. The female kobald has a special rope of entanglement and poisoned daggers. The male kobald has a special eversmoking bottle and can cast stinking cloud. The leopard is an intelligent being that can cast disguise self on itself and its two teammates. When any individual is killed, they and their special gear vanish with an explosion. Killed members revive with their special gear regenerated within 1D4 days, preferentially re-appearing in a safe location near any living members. They will not mount a pre-emptive attack unless assembled as a team.", "Poe Kensoft: A quiet, brooding blood mage who has a long rivalry with his loud, justice seeking brother, a cleric named Wynd Edlong.", "The Obelisk: This crystaline black shard appears at random and sticks around for a day. It has one large eye that watches over anyone nearby. No one knows how it gets there, but when it appears you can bet that someone is going to die. Poeple have tried to destroy, study and even worship it. All who have met a bloody demise.", "Lelouch: Dwarven warlord finds a powerful magic item that controls multiple creatures similar to dominate monster or geas. Gains a lot of mercenaries and followers and became the leader of his private army.", "Esther and Alice: A duo of child witches who are bright, subversive, charismatic, and absurdly overpowered. Their former evil mentor coached them into believing that they are the chosen ones who will bring balance to the world. The evil mentor had only modestly wicked plans and intended for the children to be puppet leaders, but the witch pair betrayed and discarded him to pursue their own grand scale deadly delusions.",
        "Irath Vanchen: A long dead egotistical and paranoid wizard researcher who became violently angry at his colleagues for stealing and taking credit for his ideas and creations. The wizard constructed a dungeon to inter his magnum opus … and enact revenge. The dungeon is designed specifically to maximize the number of humiliations visited on anyone who would enter. The dungeon even provides ample opportunities for revival just so the torrent of shaming and brutality can continue.", "Suragal: A sentient gnomic war artifice seeks to enslave gnome society. The machine intends to use the gnomes as a factory to build more of its own kind so it will not be so lonely. It has strong beliefs in mechanical superiority.", "Nature: Be it by bad luck or a curse bestowed upon them, your party ocassionally finds itself battling against exceedingly harsh elements of nature durings its travels. Perhaps those crazy druids you passed earlier were right about something...", "Bludwar: Orc maniac covered in blood, obsessed with owning the battlefield and killing anyone who gets to close to his battlefield, including his own men", "James Spader: An unassuming older human gentleman who wishes for nothing more than to keep his beloved (insert some kind of dungeon here) in peak and pristine condition, acting as something of a groundskeeper. As you go through the dungeon, recklessly smashing and tearing through its various rooms, he shows up almost impossibly behind you at every turn, asking you in an increasingly desperate manner to please keep the horseplay to a minimum. After clearing the dungeon and looting it of all its various baubles and artifacts, you turn around to leave only to run into James Spader, royally ticked off. He spouts furiously about how the young folk have no respect for the property of others, his eyes beginning to glow as this happens. You see energy begin to cracle around him as he rises into the air, still growing more impassioned in his speech. He simultaneously reaches a peak in his altitude and speech where he explodes into raw energy and flies into various contraptions (traps, moving platforms, what have you) around the dungeon, controlling them by his will in order to vanquish the trespassers (though trying to avoid damaging dungeon property).", 
        "Hermes the wizard: This old man enjoys using magic to pull pranks on and annoy people. A recent example of his work was swapping the genders of everyone in town, when someone complained he took a razor and forcibly removed their eyebrows. He was laughing all the while", "Captain Klunk: Pirate Hill Giant using a light bombard as ranged weapon and a broken mast for melee, he and his crew raid exclusively river and lakeshore villages, for he is afraid of open water. He enslaves villagefolks as rowers for his barge.", "Flarl Gravelward: A CE rock gnome who is a level [suitable number here] wizard that uses appropriate player stats. His school is abjuration. He seeks to enlarge his supply of magical items and magic spells, then learning their secrets through study. This furthers his goal of attaining godhood, as he believes that the knowlege and properties of the items will allow him to one day discover some incredibly convoluted way of becoming a god.", "To get magic items and enlarge his library of magic spells, he likes to lure adventurers into his lair by commiting small atrocities in nearby towns. He’s fine with killing people since there’s afterlives and all. His combat tactics involve cheesing every fight with glyph of warding, nuking adventurers that wander into his killroom, then jumping down with his buffs to kill the survivors. He’s employed a dozen other wizards to replace the glyphs in and repair his lair after every fight, and he pays them in duplicate magic items.", "So far he has killed a crapload of heroes, has enough magic items to make a level 20 party cry, and has a small library of looted spellbooks (including ones from other classes he can’t even use). Killing him would bring a great deal of treasure to any party, but getting to him is difficult.", 
        "If loot needs to (and in my opinion it really should) be reduced, and I hope you take this option, have the party show up while he’s using his super-furnace to melt most of the magic items (they are sometimes hard to break or melt, but his furnace is just that hot) but down into magical slag as part of an attempt at creating some kind of artifact. Alternatively, have his wizard staff grab most of the items and spellbooks and run.", "Poison Pete: He was a stall vendor who sold potions. He would pawn them off as Healing Potions but they were mostly Potions of Poison. However, If someone caught his eye and interested him he would sell them a special potion that teleported them to his lair. Then he would reveal himself as a Lich in the most gruesome of fashion", "Keran:  A mage that is terrified by his own mortality and will stop at nothing to push it back.", "He's on his way to lichdom (for a relatively low level session), or already there and looking for more efficient ways to protect his phylactery (for a higher level fight).",
        "Odo Tealeaf: A gnome bard who performs in the style of def jam poetry.", "Sallen the Fathered:  A Gnome druid who is mostly harmless aside from the army of blood thirsty chickens that he keeps on his farm. His ultimate goal is to stop the spread of city folk into more rural areas because urban sprawl destroys the livelihoods of farmers everywhere. I'd probably place him as chaotic neutral or he could even be the rare chaotic good enemy as well.", "He refers to himself as a Farm Druid, as his connection is less with the wilds and more with crop fields and livestock.", "The king of cut toungues: Once every cloven moon a jagged slate tower rises from the desert. The construct pulses rings of dark energy, creating and destroying languages: One week all the wizards speak in whistles, the next day everyone who's ever succesfully said She sells sea shells on the sea shore is speaking a language made entirely up of different intonations of the word Scrambled. These changes forge and decimate alliances and hysteria spreads. Only our party keeps their toungue, and thus their ability for any extensive communication. The heroes travel to the slate tower and rise to it's top. Where they find a maddened drow priest of Lolth. It is revealed that the tower was a temple that rose from the underdark below the desert. Lolth wanted to throw the world into a state of panic and lawlessness (Successfully, I might add) to allow Drow to dominate both the overworld and the underdark.", "Targaba Sukvergleam (Half-Elf):  Royalty who decided to take the paladins oath. Years into her journey she discovers her father isn't the (whatever royal title) It's actually an elvish necromancer that sways her into breaking her oath.", 
        "Memicville: A town sized mimic", "Sallorsarshisk Sammy The Far Reached Mind: Sammy is a being from the Far Realm. It seeks to gain purchase in the real world by any means necessary. Its preferred method is to infiltrate somone's mind and act as a sort of imaginary friend. Mystics and Warlocks are particularly suceptible to its powers, and it can even act as a patron for Warlocks. It pushes its miions to collect magic items that contain massive amounts of stored power. When it feels it has reached enough power, it will take control over the body of its minion, and cast a dangerous ritual that drains all the magic from items to power a massive portal. From there, it will enter the realm in a physical form. Its physical form resembes a humanoid roughly 25ft tall, and composed of a dark purple like substance with a texture like volcanic stone. Several apendeges extend from the body, and more can grow at will, up to 13 arm-like apendages and 8 leg-like apendages. It will turn people into thralls in order to cast a ritual to drain all the magic from a realm. After the final ritual is complete, Sammy returns to the Far Realm to begin the procress again, leaving nothing but a barren, magicless land full of thralls.", "Lost: Appears as a distressed child and asks passers by to help it find it's lost parents/home/pet etc. If the adventures agree the child follows them and cannot be lost through non-magical means. Whole accompanying the party they never find where the lost wants to go 'i swear it was around here,' 'we should ask if people have seen it' etc. They also cannot find where they want to go, so can't progress either.", 
        "The child has to be slain to break the curse, making the lost a horrible experience for a good party, they will be obliged to help before being driven mad and having to murder the child they promised to help.", "In reality the lost are children that died while lost, and their spirits linger, begging for help but dooming parties that agree.", "Skinwalker: It shall appear from the outskirts of camp and attempt to lure players away using a strong scent of blood (low DC save or poison effect). After it has one or two players isolated it will listen to what they say and become able to flawlessly replicate what they said, and rearrange those words. A few days pass and it uses those words to isolate a target with a combination of baiting tactics and getting PCs lost. Then it strikes, ideally a animal such as a cow or horse, taking it away by before returning in its fourm as if the animal just broke free. Then it watches and listens, maby killing a humanoid and taking there place once it's heard enough. Once it's established itself it acts almost as human but at night it may hunt others in the area.", 
        "Big dog: Protagonists find a magical bone with properties they don't understand. He antagonist keeps trying to get it back through kidnapping assassination etc. Villain is actually a really big dog.", "Oluchi: Oluchi is a sentient gargantuan Gelatinous Cube (his intelligence comes from a band of intellect contained in the heart of his body). His goal is to undo the curse Ghaunadaur accidentally placed on his ooze worshipers removing their sentience.", "Navec: A Lich who removes his eye and hand with every reformation and pretends to be Vecna on the Material Plane. Having gotten cold feet about this whole Undeath thing he's trying to ascend to godhood by following in Vecna's footsteps but he doesn't know how Vecna did it, so by imitating Vecna he tries to learn the method from others who know religion better than he. A master Enchanter and Illusionist he gets up to shenanigans using Mirage Arcane. Once accidentally created a coral reef of his own skeletons due to repeated reformations while his phylactery was stuck in a crushing oceanic abyss, and once was stuck in his own Ring of Mind Shielding when a Clone spell went bad.", "Calvin Waterson a.k. A Hobbes: A Rakshasa disguised as a regular Human who runs an informal assassin's guild. People send in contracts for assassination and supply payment upon completion of the contract. Assassins aren't locked into the guild, they mostly just kill whenever they can and need cash. The crux of this is that by providing a means for normal people to do Evil under a Lawful structure the Rakshasa turns everyone Lawful Evil and has a claim on their soul when they die.", "Invidia Drake: A Green Dragonborn Sorcerer who breeds with powerful Sorcerers of different races to genetically engineer powerful Sorcerer children. She sends them into the Feywild when they hatch, tended to by a Green Hag and Boggles summoned by their own loneliness, using the time dilation so that they return fully grown only five days later in the Material Plane so she can observe their Sorcerous potential.", 
        "Janus: A Cloud Giant Smiling One who pretends to be any number of patrons in a gambling den seeking to increase his personal wealth. Using his secret Giant powers to cheat he now has most of a whole town in his pocket and under his thumb", "Billy the Kid: A millennia-year old Wizard who is so bored with his immortality that he frequently wipes his memories and polymorphs into various beasts, beings, or altered versions of himself for his own amusement. His current incarnation is that of an immature 12-year-old whose idea of villainy is a series of increasingly juvenile pranks with deadly consequences. Your party has done something to attract his attention...", "King Lobster: The King Lobster is an intelligent Half-dragon/Dire Lobster sorcerer who wants to enslave the surface people. He focusses on charm spells, and enthralls whoever crosses his way, having them carry his throne/water-tank.", "Freuderick the Feral: This former wizard contracted lycantrophy while researching the effects of multiple strains of lycan infections on a single subject. However, unlike a normal 'were'-creature, Freuderick has managed a much greater level of control over his transformations. Unfortunately this newfound power also lead to a decrease in Freudericks mental faculties, and as a sideeffect, the loss of his magical abilities. Freuderick used to look down on what he called savages, but has effectively become just that. He now leads a scattered pack of various beast, beserkers and werewolves, -bears,- ravens, etc. In combat Freuderick will attack mercilessly and aggressively, he is a beast of passion though, and will likely fight untactically if a specific PC annoys or affronts him.", 
        "Doffy: A doppleganger that pretends to be a nobleman seeking to hire the party to roust the real nobleman whom the Doppelganger claims is a Doppelganger.", "Dagneg Mag'Brodthar: An orc warchief that has survived the rigors of near constant warring for 20 of his 30 years, Dagneg is through with the constant struggle. All he wants is to retire and settle down to practice his most secret joy; painting. Dagneg is a gifted painter despite having to practice in secret to avoid the shame of being outed as a soft, pinky-loving grease dauber and likely being subsequently murdered by his own men. Likewise, he would be killed by a subordinate for trying to retire. So instead, Dag is currently waging all-out war against the countryside at large in an attempt to get his entire warband killed, attracting more orcs to his banner in the process. Dag has two tame bullettes that he uses for mounts and his mother Kurfulta travels everywhere with him. He's a clever orc with a strong will and a dark sense of humour. He would be willing to make a mutually beneficial deal with adventurers.", "Nyera, Lady of Cowardice: Champion of (insert evil deity here), she appears to be a compassionate and caring noble. In reality she is a demonic spirit who controls people's shameful feelings, secrets, and fears against her foes until their minds shatter. Once she starts to work her dark enchantments on a target, she will kindly ask if she can end their suffering. When they targets have fully succumbed to their deeps secrets and shames, they will beg for death, and she will grant that wish with a single sword stroke. Her special attacks during combat cause exhaustion as targets lose their will to live.", 
        "Nyera is interesting because she can not only blend in with humanoids, she might even appear as a compassionate mother Teresa type. For short periods of time, she could even become a well known and beloved member of the nobility, but her true nature would make that difficult long term. Could be summoned by another villain looking to summon some powerful deity (since actually summoning a deity is a lvl 15+ campaign which is rare to see) or summoned to the mortal realm for some other reason.", "Astragon the Mad: This Wizard has studied planar cosmology for his entire adult life, seeking the aid of Devils, Illithids, ancient Aboleths and the like to suppliment his knowledge. Particularly, Astragon finds the formation of the Inner Energy Planes to be abberant. He seeks to rebuild the Elemental Chaos from the pieces we have today, namely the Elemental and Energy Planes. While not neccesarily malicious, Astragon will stop at nothing to see what he sees as the true cosmological order restored, regardless of the destruction and death that would be casued by combining these worlds into one.", "Squimbly Dooblebop the III: A gnome lord who continues his families secretive gnome mafia, the bimbles, who controls any illegal trade/trafficking. The Bimbles end goal is to overthrow the capital and bring glory to his families name and organization.", "Dick Dastardly: A mustache twiddling, black cape wearing, tie girls to train tracking, nefarious, vilanous, henous, dastardly villan. Could be making a convoluted assasanation scheme, could be bank robbing, really anything a western villan would be seen doing.", "Octavius the Artist: A spellcaster artist obsessed with making the perfect piece of art.  He lures adventuring parties into his manor and then subjects them to a series of traps, puzzles, and encounters, which he paints meticulously.  When the party inevitably fails to fit his artistic vision perfectly, he flies into a rage and attempts to kill the stains upon his beautiful narrative.", 
        "The Great Enthusio: An overzealous stage magician turned wizard, The Great Enthusio seeks great fame and his own recklessness often gets in the way of that. Wears fancy outfits, has no fashion sense, introduces his henchmen as MY BOLD COMPANIONS or THESE EXTRAODINARY ASSISTANTS", "Damien Lockeheart: A lich who fancies himself a god who will do whatever it takes to unite the world under his fist. However he does this in preparation of a greater, Eldritch threat that lies somewhere in the world, waiting to resurface. He also constantly reincarnates a longtime enemy, Stoa Lockeheart to torture and fight against out of a never ending hatred.", "Enzoth Lajore: A Rakshasa in the form of an old human cardinal with white hair, a high forehead and a chin-beard who wears purple colors and robes. He took the role of this holy man a decade ago after killing the original Enzoth, a friendly but seclusive man. Since then he has established himself as the owner in the shadows of numerous exclusive houses of pleasure. This way he can gather dirty info on the numerous noblemen and merchants of the city, as well as get a feast of human flesh from time to time when a new girl applies or a noble reclines to work with him.", 
        "Archfey Nifrim: Elven Wildmagic sorcorer who found their way to the material plane and is accompanied two elf guards. From living in the feywild most of her life, learned to become one with the forest. However fearful of all non-fey creatures who threaten her home. Her goal is to turn the material plain into a second feywild plane by merging the two", "Oinkbane: is a were-boar assassin. He is notable for being subtle and even devious in his approach to his target while making it clear that he is on the way. His battle cries are TOO SUBTLE! and SNEAK ATTACK!", "Zoey Zeitgeist, Keeper of the Splintered Eye: When she was young, she enjoyed the stories about dark and mysterious monsters in the hills and deep in the darkest parts of the jungle. Her father used to tell her about The Witch Hag and Krin the Profane, stories about power corrupting the vain and greedy—stories to help children grow up rightly.", "She loved them. She imagined, wickedly and in that way children are innocent of imagining, growing tall and powerful and finding dark powers and ruling over those that bullied her in the town market or those that hurt others. She’d be a glorious and just monster. If there was any point where it could be said she stepped onto the path that took her here, it was the moment she realized one could aspire to be a just monster. A noble horror. A necessary evil.", "There would be more coming. More young and idealistic men and women. Mere boys and girls, really. Thinking they knew better and best. They’d come for her. They’d come to devour her. They’d kill the beast she was and think themselves righteous. And they’d be wrong. Their kind was made of power, and power only corrupts. Not that she was ready to go, she wasn’t. She’d murder them all as they came as she always did. Further cementing her reputation. The folktales would continue to call her a monster, but dispatching these heroes was the last noble act she knew. She was a just monster after all.", 
        "Aemon Damienno: He is a paladin focused on burning away all that is not good in the world. He will kill petty thieves, tieflings. (innocent or not), and anyone who stands in his way.", "Xemnas the Dimensional Remnant : A dragonborn warlock, chaotic good Has existed in countless dimensions, which he's all saved from inevitable doom, and committed suicide immediately after for some greater cause. It just so turns out that your PC party has something to do with every dimensions' demise, and he's decided to kill you just in case that would apply to this one as well", "Erebhon, the endless smile: A Dwarf with eyes that convey a sense of madness, Speaking to the party only in jokes he will always be attempting to make them laugh, his sense of humour over the years has become twisted and warped, he finds grisly murder absolutely hilarious and thinks that the party should too", "Meluvius, The Dark God of Chaos: A Dark god worshipped by a cult... Except he's not really a god, but merely an above-average mage who impressed some people a long time ago, before being banished for half a millennia. His cult's going to be the threat here, not him.", 
        "Grublik the Unwashed: An Orc Warrior, who often goes on on raiding parties with a team of Goblins, killing innocent villagers and innocent bandits alike. He dislikes bees, the colour purple, and being set on fire. He loves his pet Cougar, Mr Mittens, and his Goblin Roomate, Gruncho the Mildly Clerical.", "Kein Spa: Human male who hangs around the orphanage/church and abducts children. The children aren't phyiscally harmed, or so they claim.", "Steven Richardson: Steven approaches the party as an average NPC and helpful town person. He can be used for explository purposes but by night he is a very skilled hired assassin who is hired to kill the party. He tries to kill them off one by one. He doesn't kill anyone he isn't paid to kill. Think Ned Flanders in Dark Brotherhood. I had him be an Human assassin", "Greg the Skeleton: Greg the Skeleton- He's a skeleton who is alot more powerful than he looks. He can do like 1 damage, but he has like 99 health. He wears a wolf skin hat and carries a stuffed wolf under his arm. Thats his weapon.", "Daigon the Wicked: An immortal fey, Daigon has long since lost fulfillment in her own life. Malicious and spiteful, she takes wicked pleasure in masquerading about the material plane as a harmless creature. Using illusions and enchantments she convinces unfortunate mortals to take her in as a pet. Once in their good graces, Daigon will plot the ultimate demise of her victim. She takes the greatest pleasure in schemes that cause her victim to project blame upon someone else. Nobles, Monarchs, and persons in power are particularly enticing, as she can manipulate them to cause widespread suffering.",

    ]
    document.getElementById("Premade").innerHTML = searchArray(premades);
};

